import { Injectable } from '@angular/core';
import {DomSanitizer, SafeHtml} from "@angular/platform-browser";

@Injectable({
  providedIn: 'root'
})
export class HtmlMapperService {

  constructor(private sanitizer: DomSanitizer) { }

  public asImage(char: string): SafeHtml {
    let rc = char;
    let base64 = undefined;
    switch (char) {
      // case 'X':
      //   base64 = '';
      //   break;
      case '🌿':
        base64 = '';
        break;
      case '🌱':
        base64 = '';
        break;
      case '🥀':
        base64 = '';
        break;
      case '🌻':
        base64 = '';
        break;
      case '🍀':
        base64 = '';
        break;
      case '☘':
        base64 = '';
        break;
      case '🌷':
        base64 = '';
        break;
      case '🌹':
        base64 = '';
        break;
      case '🌵':
        base64 = '';
        break;
      case '🌴':
        base64 = '';
        break;
      case '🏕':
        base64 = '';
        break;
      case '🎪':
        base64 = '';
        break;
      case '🏯':
        base64 = '';
        break;
      case '🏰':
        base64 = '';
        break;
      case '🌧':
        base64 = '';
        break;
      case '🌦':
        base64 = '';
        break;
      case '🌲':
        base64 = '';
        break;
      case '🌳':
        base64 = '';
        break;
      case '🏭':
        base64 = '';
        break;
      case '⛅':
        base64 = '';
        break;
      case '🏡':
        base64 = '';
        break;
      case '🏠':
        base64 = '';
        break;
      case '🏘':
        base64 = '';
        break;
      case '☀':
        base64 = '';
        break;
      case '☁':
        base64 = '';
        break;
      case ' ':
        rc = '&nbsp;';
        break;
    }
    if (base64) {
      rc = '<img alt="'+char+'" src="data:image/gif;base64,' + base64 + '" width="25" height="25"/>'
    }
    return this.sanitizer.bypassSecurityTrustHtml(rc);
  }

}
